/* Ticket #71 Allowance Notification */
update `sys_template` set `design` = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" ><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\tReminder: Tomorrow is Allowance Day for  ${childNames}!\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>\r\n\t  This is to let you know that allowance will be posted on FiftyP tomorrow for ${childNames}, along with other earnings or gifts that may be scheduled. Your credit card will not be charged. For more details, or to make changes to this posting, log in to <a href=''http://www.fiftyp.com''>www.fiftyp.com</a> and select their name.  \r\n\t<br/><br/><p>\tThank you for being part of our community. We hope your family is enjoying FiftyPs unique approach to teaching kids financial and social responsibility ... and that you will tell your friends. The bigger our FiftyP community, the greater the interest in what it means to be a money-wise, caring kid.</p>\r\n\t<p> Please let us know if you have any questions or suggestions for us. We are always looking to improve and enhance FiftyP. You can drop us an email at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a> or log in to FiftyP and select Feedback from the Helpful Links menu. </p>\r\n\t  <p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tTeam FiftyP<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t  </p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a><br>\r\n    </font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' where `sys_template_id` = 'ALLOWANCE_NOTIFICATION';

/* Ticket #71 Allowance Notification (grammar fix) */
update sys_template set design = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" ><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\tReminder: Tomorrow is Allowance Day for  ${childNames}!\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>\r\n\t  This is to let you know that allowance will be posted on FiftyP tomorrow for ${childNames}, along with other earnings or gifts that may be scheduled. Your credit card will not be charged. For more details, or to make changes to this posting, log in to <a href=''http://www.fiftyp.com''>www.fiftyp.com</a> and select ${personalPronoun} name.  \r\n\t<br/><br/><p>\tThank you for being part of our community. We hope your family is enjoying FiftyPs unique approach to teaching kids financial and social responsibility ... and that you will tell your friends. The bigger our FiftyP community, the greater the interest in what it means to be a money-wise, caring kid.</p>\r\n\t<p> Please let us know if you have any questions or suggestions for us. We are always looking to improve and enhance FiftyP. You can drop us an email at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a> or log in to FiftyP and select Feedback from the Helpful Links menu. </p>\r\n\t  <p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tThe FiftyP Team<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t  </p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a><br>\r\n    </font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' where sys_template_id = 'ALLOWANCE_NOTIFICATION';

