/* -------------- SHARE STORE START ----------------------*/
/* Share Store SQL Script */

DROP TABLE IF EXISTS `share_points_activity`;
CREATE TABLE `share_points_activity` (
  `id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(100) NOT NULL,
  `point_value` INTEGER UNSIGNED NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)
)
ENGINE = InnoDB;

DROP TABLE IF EXISTS `share_store_item`;
CREATE TABLE `share_store_item` (
  `id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `cause_id` INTEGER NOT NULL,
  `name` VARCHAR(100) NOT NULL,
  `icon` VARCHAR(50) NOT NULL,
  `point_value` INTEGER NOT NULL DEFAULT 0,
  `globe_pos_x` INTEGER NOT NULL DEFAULT 0,
  `globe_pos_y` INTEGER NOT NULL DEFAULT 0,
  `thank_you_msg` TEXT,
  PRIMARY KEY (`id`),
  CONSTRAINT `FK_share_store_item_cause` FOREIGN KEY `FK_share_store_item_cause` (`cause_id`)
    REFERENCES `cause` (`cause_id`)
    ON DELETE RESTRICT
    ON UPDATE RESTRICT
)
ENGINE = InnoDB;

DROP TABLE IF EXISTS `child_share_store_item`;
CREATE TABLE `child_share_store_item` (
  `id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `child_profile_id` BIGINT(20) NOT NULL,
  `share_store_item_id` BIGINT(20) UNSIGNED NOT NULL,
  `purchase_date` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  CONSTRAINT `FK_child_share_store_item_child_profile` FOREIGN KEY `FK_child_share_store_item_child_profile` (`child_profile_id`)
    REFERENCES `child_profile` (`child_profile_id`)
    ON DELETE RESTRICT
    ON UPDATE RESTRICT,
  CONSTRAINT `FK_child_share_store_item_share_store_item` FOREIGN KEY `FK_child_share_store_item_share_store_item` (`share_store_item_id`)
    REFERENCES `share_store_item` (`id`)
    ON DELETE RESTRICT
    ON UPDATE RESTRICT
)
ENGINE = InnoDB;

ALTER TABLE `child_profile` ADD COLUMN `points` INTEGER NOT NULL DEFAULT 0 AFTER `show_pledge`;

