SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">  <tr>    <td style="border-bottom:1px solid #7B2B83">      <table width="100%" border="0">        <tr>          <td><img src="cid:logo_parent.gif" width="294" height="84"></td>          <td align="right" style="padding:55px 10px 0px 0px" ><font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"><b>			Membership Receipt		  </b></font></td>        </tr>    </table></td>  </tr>  <tr>    <td style="padding:20px;font-size:12px; font-family:verdana;">		Dear ${userName},<br/><br/> Thank you for joining ThreeJars! <br/><br/>		Your Family Membership payment of $29.95 has been processed. In addition, we <br/>deposited $5 into ${numOfKids} Share Account. Here are more <br/>details:<br/><br/>		Membership Term: ${membershipTerm} <br/>		Parent Login ID: ${parentLoginId} <br/><br/>		Transaction #: ${transactionId}<br/>		Payment method: ${cardDetails}<br/><br/>		Please note that because of our monthly commitment to supporting charitable<br/> causes, this membership is non-refundable.<br/><br/>		If you have any questions, please check our FAQs or email us at <br/><a href=''mailto:${supportEmail}''>${supportEmail}</a>. Thanks again for putting your ${pronounString} on the right path!<br/><br/>		Sincerely,<br/>		The ThreeJars Team<br/>		<a href=''${siteURL}''>${siteURL}</a><br>	 </td>  </tr>  <tr>    <td bgcolor="#7B2B83" style="padding:5px"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">For more information please contact our support team at </font><a href=''mailto:${supportEmail}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>    </td>  </tr></table></body>'
WHERE sys_template_id = @systemTemplateId ;

