SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">  <tr>    <td style="border-bottom:1px solid #7B2B83">      <table width="100%" border="0">        <tr>          <td><img src="cid:logo_parent.gif" width="294" height="84"></td>          <td align="right" style="padding:55px 10px 0px 0px" ><font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"><b>			Investment request from ${childName} (Action required)    		  </b></font></td>        </tr>    </table></td>  </tr>  <tr>    <td style="padding:20px;font-size:12px; font-family:verdana;">		Dear ${userName},<br/><br/>		${childName} would like to make $${profitAmount} by investing in a ${investment} for<br/>		${timePeriod}. <br/><br/>Here''s how to respond by email:<br/><br/><b>A.</b>Click <b>Reply</b>, then type <b>"Approve"</b> or <b>"Reject"</b> in the body of your email. Then click <b>Send</b>.<br/>Please note: DO NOT delete the text of the original email.<br/><br/><b>B.</b> Even simpler:<br/><a href=''${approvalLink}''>Click Here to Approve</a><br/>or<br/><a href=''${rejectionLink}''>Click Here to Reject</a><br/><br/>If you prefer, you can respond on the ThreeJars website. Just log in to your account at <a href=''${siteURL}''>${siteURL}</a> and check your message center.<br/><br/>		Sincerely,<br/><br/>		The ThreeJars Team<br/><br/>Request #${requestId}.<br/>			 </td>  </tr>  <tr>    <td bgcolor="#7B2B83" style="padding:5px"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">For more information please contact our support team at </font><a href=''mailto:${supportEmail}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>    </td>  </tr></table></body>'
WHERE sys_template_id = @systemTemplateId ;

select @systemTemplateId:=(select sys_template_id from sys_template where description = "Email template for spend request");
UPDATE sys_template
SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">  <tr>    <td style="border-bottom:1px solid #7B2B83">      <table width="100%" border="0">        <tr>          <td><img src="cid:logo_parent.gif" width="294" height="84"></td>          <td align="right" style="padding:55px 10px 0px 0px" ><font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"><b>			Spend request from ${childName} (Action required)  		  </b></font></td>        </tr>    </table></td>  </tr>  <tr>    <td style="padding:20px;font-size:12px; font-family:verdana;">		Dear ${userName},<br/><br/>		${childName} would like to buy a $${amount} ${cardName} gift card with money from ${pronoun} Spend Account.<br/><br/>If you approve, the price will be charged to the credit card on file: ${ccType}, ${ccDisplayNumber}, and shipped to:<br/><br/>${userName}  ${lastName}<br/>${streetName}<br/>${city}, ${zip}<br/><br/>Here are the details: <br/><table width="40%"><tr><td>${cardName}:</td><td width="20%" align="right"> $${amount}</td></tr><tr><td>Shipping & Handling:</td><td width="20%" align="right"> $${shippingHandling}</td></tr><tr><td>Merchant Charge:</td><td width="20%" align="right"> $${merchantCharge}</td></tr><tr><td></td><td width="20%" align="right"> -------- </td></tr><tr><td>Total:</td><td width="20%" align="right"> $${total} </td></tr></table><br/><br/> Shipping & Handling: This is the exact amount charged by our fulfillment provider to get the card to you. It covers the cost of the envelope, the letter, the card, stuffing the envelope, and a $0.44 First Class stamp. ThreeJars does not profit from this charge.<br/><br/>Merchant Charge: Credit card companies charge up to 2.9% of the purchase value and shipping & handling costs, plus $0.30. ThreeJars does not profit from this charge.<br/><br/><b>To respond to this request by email:</b><br/><br/><b>A.</b>Click <b>Reply</b>, then type <b>"Approve"</b> or <b>"Reject"</b> in the body of your email. Then click <b>Send</b>.<br/>Please note: DO NOT delete the text of the original email.<br/><br/><b>B.</b> Even simpler:<br/><a href=''${approvalLink}''>Click Here to Approve</a><br/>or<br/><a href=''${rejectionLink}''>Click Here to Reject</a><br/><br/>If you prefer, you can respond on the ThreeJars website. Just log in to your account at <a href=''${siteURL}''>${siteURL}</a> and check your message center.<br/><br/>		Sincerely,<br/><br/>		The ThreeJars Team<br/><br/>Request #${requestId}.<br/>	 </td>  </tr>  <tr>    <td bgcolor="#7B2B83" style="padding:5px"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">For more information please contact our support team at </font><a href=''mailto:${supportEmail}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>    </td>  </tr></table></body>'
WHERE sys_template_id = @systemTemplateId ;

select @systemTemplateId:=(select sys_template_id from sys_template where description = "Email template for share request");
