select @systemTemplateId:=(select sys_template_id from sys_template where description = "Email template for new paying user");
UPDATE sys_template
SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><style type="text/css"><!--.verdana {	font-family: Verdana, Geneva, sans-serif;}--></style></head><div><body ><table  width="744" border="0">	<tr><td><table width="744" border="0" align="left" cellpadding="0" cellspacing="0" style="border:15px solid #7B2B83;border-bottom:0px;">			  <tr>			    <td width="635" style="border-bottom:0px solid #7B2B83">			      <table width="100%" border="0">			        <tr>			          <td width="50%"><img src="cid:logo_parent.gif" width="294" height="84"></td>  <td width="50%" align="center" valign="top" style="padding:15px 10px 0px 0px;font-family:verdana;font-size:12px;" >	 <br /> Welcome to ThreeJars!<br/>	    We''re glad you''re here!	 </td> </tr>	 </table></td>	 </tr>	  <tr >	 <td style="padding-left:15px;font-size:12px; font-family:verdana;">	<p><b>Thanks for joining our growing community of families raising money-wise,   caring kids.</b> <br />    To help you jumpstart your kids'' progress here''s what   other ThreeJars parents recommend:</p>				      <p><img src="cid:welcome_email_logo.jpg" width="600" height="508"><br />				       <br/>Because ThreeJars is also about sharing, your kids can start to make a difference   right away. <STRONG>We give away 50% of our profits </STRONG>to charity so   encourage your kids to rate our <A href="${siteURL}/${causeMonth}" target="_blank">four featured causes</A> to help direct ThreeJars''s monthly donation.<br/></p>				      <p>Let ThreeJars begin working for your family today. To log in, go to <A href="${siteURL}" target="_blank">${siteURL}</A>. And, click <A href="${siteURL}/swf/parentDemo/parentDemo.html" target="_blank">here</A> if you haven''t watched our Parent demo yet.<BR>				        <br/>				        Our best to your family and thanks again for joining,<BR>				      The ThreeJars Team<br/><br />				        ************************************************************************************<BR>				        <b>A   Special Offer for ThreeJars Members: </b>ThreeJars has teamed up with Scholastic   for a great offer! Get 20% off your purchase at the <A href="http://store.scholastic.com/webapp/wcs/stores/servlet/HomeView?storeId=10052&catalogId=10051" target="_blank">Scholastic Store Online</A>. &nbsp;Use code FTP09 through ${scholasticDate} at   checkout on any purchase of $49 or more!<BR>				        <br>				    </p>				</td>			  </tr>			  <tr>			    <td bgcolor="#7B2B83" style="padding:5px;border:0px solid #7B2B83;"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">					For more information please contact our support team at </font><a href=''mailto:${supportEmail}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>			    </td>			  </tr>			</table>	</td>	</tr>	<tr>		<td>			<div style="padding-left:15px;font-family:verdana;font-size:12px;">            <br />				<p>OPT OUT: You are receiving this message because you are a member of ThreeJars.com. <BR>				  If you do not wish to receive these messages in the future,<BR>				  please send   us an email at <A href="mailto:${supportEmail}" target="_blank">${supportEmail}</A> with unsubscribe in the subject   line.<BR>				  <BR>				  You are currently listed in our mailing list as: <A href=mailto:${userName?split("@")[0]}@ThreeJars.com target="_blank">${userName?split("@")[0]}@ThreeJars.com</A><BR>				  <BR>				  ThreeJars,   Inc.<BR>				  114 Washington Street<BR>				  South Norwalk, CT 06854<BR></p></div>	    </td>	</tr></table></body>'
WHERE sys_template_id = @systemTemplateId ;

