SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">  <tr>    <td style="border-bottom:1px solid #7B2B83">      <table width="100%" border="0">        <tr>          <td><img src="cid:logo_parent.gif" width="294" height="84"></td>          <td align="right" style="padding:55px 10px 0px 0px" nowrap="nowrap"><font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"><b>${request}</b></font></td>        </tr>    </table></td>  </tr>  <tr>    <td style="padding:20px;font-size:12px; font-family:verdana;">	<p>Dear ${parentName},</p>	<p>		<b>A request is awaiting your approval at ThreeJars.com!</b>		<br/> 		<b>${childName}</b> ${childRequest} For details, log in to <a href=''${siteURL}''>${siteURL}</a> and check your message center. oR you can reply to this mail and respond as approve or reject either approving or rejecting the request.</p>\r\n<p>Main message:</p><br/>\r\nClick Reply, then type �Approve� or �Reject� in the body of your email. Then click Send. \r\nPlease note: DO NOT delete the text of the original email.\r\n\r\nB. Even simpler: \r\n	<a href =${approvalLink}>Click Here to Approve</a>\r\n	or\r\n	<a href =${rejectionLink}>Click Here to Reject</a>\r\nWith best wishes,<br>Team ThreeJars<br><a href=''${siteURL}''>${siteURL}</a><br>			</td>  </tr>  <tr>    <td bgcolor="#7B2B83" style="padding:5px"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">For more information please contact our support team at </font><a href=''${siteURL}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>    </td>  </tr></table></body>'
WHERE sys_template_id = @systemTemplateId ;

select @systemTemplateId:=(select sys_template_id from sys_template where description = "Email template for suggestcause");
UPDATE sys_template
SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd">\r\n<html>\r\n<head>\r\n<title>ThreeJars</title>\r\n<meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1">\r\n</head>\r\n<body>\r\n<br>\r\n<br>\r\n<table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">\r\n  <tr>\r\n    <td style="border-bottom:1px solid #7B2B83">\r\n      <table width="100%" border="0">\r\n        <tr>\r\n          <td align="center"  nowrap="nowrap"><font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"><b>ThreeJars site suggested cause from child</b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style="padding:20px;font-size:12px; font-family:verdana;">\r\n	<p><b> ${userName}</b> suggested the following cause: </b></p>\r\n	<p> Location: <b> ${location}</b>.</p>\r\n	<p> Category: <b> ${category}</b>.</p>\r\n	<p> Cause: <b> ${cause}</b>.</p>\r\n	\r\n	<p>This suggested cause is generated from the machine <b>${clientAddress}</b> on <b>${date}</b>\r\n	</td>\r\n  </tr>\r\n\r\n</table>'
WHERE sys_template_id = @systemTemplateId ;

select @systemTemplateId:=(select sys_template_id from sys_template where description = "Charging email for gift card confirmation");
UPDATE sys_template
SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">  <tr>    <td style="border-bottom:1px solid #7B2B83">      <table width="100%" border="0">        <tr>          <td><img src="cid:logo_parent.gif" width="294" height="84"></td>          <td align="right" style="padding:55px 10px 0px 0px" ><font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"><b>			Gift Card Order     		  </b></font></td>        </tr>    </table></td>  </tr>  <tr>    <td style="padding:20px;font-size:12px; font-family:verdana;">		${purchaseDate?string("MMMM d, yyyy")}<br/><br/>		Dear ${parentName},<br/><br/>		We''re writing to confirm your recent gift card purchase. Your payment has been<br/> processed. You can expect delivery by first-class mail within 7 to 10 business<br/> days.<br/><br/>		Here are the details:<br/><br/>		Purchase date: ${purchaseDate?string("MMMM d, yyyy")}<br/><br/>		Confirmation#: ${orderNumber}<br/><br/>		Item: ${giftName} gift card<br/>		Value: $${giftPrice}<br/>		Shipping, Handling & Merchant Charge: ${shippingCost}<br/>		Total: $${total}<br/>		Payment method: ${ccType}, ${ccDisplayNumber}<br/><br/>		Shipping Address:<br/>		${buyerName}<br/>		${streetName}<br/>		${cityName}<br/><br/>		For questions about your child''s gift card purchase, or to learn more about our<br/>retail partners, go to		<a href=''${siteURL}/faq''>${siteURL}/faq</a>. Still have questions? Email us at<br/>		<a href=''${siteURL}/support''>${siteURL}/support</a>.<br/><br/>		Sincerely,<br/>		The ThreeJars Team<br/>		<a href=''${siteURL}''>${siteURL}</a><br>	 </td>  </tr>  <tr>    <td bgcolor="#7B2B83" style="padding:5px"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">For more information please contact our support team at </font><a href=''mailto:${supportEmail}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>    </td>  </tr></table></body>'
WHERE sys_template_id = @systemTemplateId ;

