SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">  <tr>    <td style="border-bottom:1px solid #7B2B83">    <table width="100%" border="0">        <tr>          <td> <img src="cid:logo_parent.gif" width="294" height="84"></td>       <td align="right" style="padding:55px 10px 0px 0px" nowrap="nowrap"> <font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"> <b> ThreeJars request ${approveRejectStatus} confirmation.</b></font></td>        </tr>    </table></td>  </tr>   <tr>    <td style="padding:20px;font-size:12px; font-family:verdana;"> <p>${message} <br/><br/>  A message has been sent to ${childName}''s homepage.<br/> </p>Sincerely,<br>Team ThreeJars<br> <a href=''${siteURL}''>${siteURL}</a><br>                 </td>  </tr>  <tr>    <td bgcolor="#7B2B83" style="padding:5px"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">For more information please contact our support team at </font><a href=''mailto:${supportEmail}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>    </td>  </tr></table></body>'
