UPDATE sys_template
SET design = '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN""http://www.w3.org/TR/html4/loose.dtd"><html><head><title>ThreeJars</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><table width="600" border="0" align="center" cellpadding="0" cellspacing="0" style="border:1px solid #7B2B83">  <tr>    <td style="border-bottom:1px solid #7B2B83">      <table width="100%" border="0">        <tr>          <td><img src="cid:logo_parent.gif" width="294" height="84"></td>          <td align="right" style="padding:55px 10px 0px 0px" ><font color="#F68B1F" size="2" style="font-size:14px;" face="verdana"><b>			Share request from ${childName} (Action required)  		  </b></font></td>        </tr>    </table></td>  </tr>  <tr>    <td style="padding:20px;font-size:12px; font-family:verdana;">		Dear ${userName},<br/><br/>		${childName} wants to donate $${amount} from ${pronoun} Share Account to ${causeName}, a project sponsored by		${charityName}. <br/><br/>If you approve, the donation will be charged to the credit card on file: ${ccType}, ${ccDisplayNumber}.<br/><br/>Here are the details: <br/><table width="40%"><tr><td>Donation Value:</td><td width="20%" align="right"> $${amount}</td></tr><tr><td>Merchant Charge: </td><td width="20%" align="right"> $${merchantCharge}</td></tr><tr><td></td><td width="20%" align="right"> -------- </td><tr><td>Net Donation:</td><td width="20%" align="right"> $${total} </td></tr></table><br/><br/>  Please note: Credit card companies charge up to 2.9% of your donation plus $0.30. The designated Charity will receive the Donation Value less the Merchant Charge. For tax purposes you will receive a receipt for the full Donation Value. ThreeJars does not profit from the Merchant Charge.<br/><br/><a href=''${approvalLink}''>Click Here to Approve<img src="cid:check.png"></a><br/><br/>or<br/><br/><a href=''${rejectionLink}''>Click Here to Reject<img src="cid:X.png"></a></b><br/><br/> You can also respond by clicking <b>Reply</b> and typing "Approve" or "Reject" in the body of your email.<br/><br/>If you prefer, you can respond on the ThreeJars website. Just log in to your account at <a href=''${siteURL}''>${siteURL}</a> and check your message center.<br/><br/>		Sincerely,<br/>		The ThreeJars Team<br/>	 </td>  </tr>  <tr>    <td bgcolor="#7B2B83" style="padding:5px"> <font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">For more information please contact our support team at </font><a href=''mailto:${supportEmail}''><font  style="font-size:11px; line-height:16px" face="verdana" color="#ffffff">${supportEmail}</font></a><br>    </td>  </tr></table></body>'
WHERE sys_template_id = @systemTemplateId ;
/* end of 1209*/

/* #1222 - updating FiftyP to ThreeJars in the parentwizard text */
select @causeId:=(select cause_id from cause where name = "Treetops for Tit&#237;s" and month="6");
UPDATE cause 
SET parent_wizard_text ='ThreeJars members are preserving the habitat of the endangered tit&#237; monkey by linking their patches of forest with new forest growth.'
WHERE cause_id =@causeId;

select @causeId:=(select cause_id from cause where name = "Safe Stomping Grounds" and month="6");
UPDATE cause
SET parent_wizard_text ='ThreeJars members are building a sanctuary for rescued bull elephants, so these majestic animals can enjoy a life of dignity and peace.'
WHERE cause_id =@causeId;

select @causeId:=(select cause_id from cause where name = "Share the Music" and month="6");
UPDATE cause 
SET parent_wizard_text ='ThreeJars members are putting musical instruments into the hands of students in underserved schools and community music programs.'
WHERE cause_id =@causeId;

select @causeId:=(select cause_id from cause where name = "Prevent Malaria" and month="6");
UPDATE cause 
SET parent_wizard_text ='ThreeJars members are protecting children from the mosquitoes that transmit malaria. An inexpensive bed net can prevent this life-threatening disease.'
WHERE cause_id =@causeId;
/* End of #1222 */

