/* Release 1.7 sql file */
/*    
1. Change Parent Summary to Family Account Summary
2. Change Team FiftyP to The FiftyP Team
*/
UPDATE `sys_template`  SET `sys_template`.`design` = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" nowrap=\"nowrap\"><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\tOrder Confirmation of Gift card\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>We''re writing to confirm your recent gift card purchase. Your payment has been processed. You can expect delivery by first-class mail within 7 to 10 business days. </p>\r\n\t<p>\r\n\t\tPurchase date: ${purchaseDate?string(\"MMMM d, yyyy\")}\r\n\t\t<br>\r\n      Confirmation #: ${orderNumber}</p>\r\n\t  <p>Item: ${giftName} gift card <br>\r\n\t  Value: $${giftPrice} <br>\r\n\t  Shipping &amp; handling: $${shippingCost} <br>\r\n\t  Total: $${total}<br>\r\n\t  Method of payment: ${paymentMethod}, \r\n\r\n\r\n card #${ccDisplayNumber}</p>\r\n\t  <p> Shipping Address: <br>\r\n        ${buyerName} <br>\r\n        ${streetName} <br>\r\n        ${cityName} <br>\r\n      </p>\r\n\t  <p>For questions about your child''s gift card purchase, or to learn more about our retail partners, go to www.fiftyp.com/faq . Still have questions? Email us at www.fiftyp.com/support . </p>\r\n\t  <p>Please keep us informed of any changes to your email address. You can update your information by logging in to your FiftyP account and clicking on �Your Profile�.</p>\r\n\t  <p>Thanks for supporting your child in this purchase. Allowing our kids to make spending decisions gives them a great opportunity to practice money management skills. </p>\r\n\t  <p>&nbsp;\t  </p>\r\n\t  <p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tThe FiftyP Team<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t  </p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a><br>\r\n    </font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' WHERE `sys_template`.`sys_template_id`='ORDER_CONFIRMATION_GIFT_CARD';
UPDATE `sys_template`  SET `sys_template`.`design` = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" nowrap=\"nowrap\"><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\tOrder Confirmation of Membership Card Purchase\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>\r\n\t  Thank you for your recent purchase of FiftyP Membership Cards. Your payment has been processed. You can expect delivery by first-class mail within 7 business days.    \t<p>Here are the details:</p>\r\n\t<p>Purchase date: ${purchaseDate?string(\"MMMM d, yyyy\")} <br>\r\n\tOrder #: ${orderNumber}</p>\r\n\tQuantity: ${quantity} Membership Cards <br>\r\n\tUnit Price: $${unitPrice} per card <br>\r\n\tSubtotal: $${subtotal} <br>\r\n\tShipping &amp; handling: $${shippingHandling} <br>\r\n\tTotal: $${total} <br>\r\n\tPayment method: ${paymentMethod}, card #${ccDisplayNumber}\r\n\t<p>Shipping Address: <br>\r\n\t  ${buyerName} <br>\r\n\t  ${streetName} <br>\r\n\t  ${cityName}</p>\r\n\t<p>To learn more about FiftyP, please visit us at <a href=\"http://www.fiftyp.com\">www.fiftyp.com </a> or check our FAQs at <a href=\"http://www.fiftyp.com/faq\">www.fiftyp.com/faq </a>. Still have questions? Email us at www.fiftyp.com/support . </p>\r\n\t<p>Thank you for introducing FiftyP to your family, friends, colleagues and customers. We appreciate your support. </p>\r\n\t<p>&nbsp;\t  </p>\r\n\t  <p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tThe FiftyP Team<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t  </p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a><br>\r\n    </font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' WHERE `sys_template`.`sys_template_id`='ORDER_CONFIRMATION_MEMBERSHIP_CARD_TRANSACTION';
UPDATE `sys_template`  SET `sys_template`.`design` = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" nowrap=\"nowrap\"><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\tConfirmation: Child added to Family account\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>\r\n\t  Thank you for adding another child to your FiftyP account. You have activated a free membership as of ${activationDate?string(\"MMMM d, yyyy\")}.    \r\n\t<p>This free membership introduces your child to basic money management skills and to charitable causes. FiftyP also makes it easy for you to keep track of ${personalPronoun} allowance and extra earnings.</p>\r\n\t<p>If you''d like ${personalPronoun} to get even more out of FiftyP, consider switching to a one-year or three-year membership plan. Both give kids hands-on experience with saving, spending, and sharing�the best way to learn. Now''s the time to help ${personalPronoun} form healthy habits!</p>\r\n\t<p>You can upgrade your child''s membership at any time: Log in to your FiftyP account at www.fiftyp.com and click the <strong>Membership </strong>button.</p>\r\n\t<p>Questions? Please email us at <a href=\"mailto:support@fiftyp.com\">support@fiftyp.com </a>. </p>\r\n\t<p>&nbsp;\t  </p>\r\n\t  <p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tThe FiftyP Team<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t  </p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a><br>\r\n    </font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' WHERE `sys_template`.`sys_template_id`='ORDER_CONFIRMATION_NEW_CHILD_FREE';
UPDATE `sys_template`  SET `sys_template`.`design` = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" nowrap=\"nowrap\"><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\tConfirmation: Child added to Family account\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>\r\n\t  Thank you for adding another child to your FiftyP account. Our FiftyP community grows stronger with each new member!    \r\n\t<p>This new ${membershipType} membership has been activated as of ${activationDate?string(\"MMMM d, yyyy\")}. Have your child jump in and get started!</p>\r\n\t<p>If at any time you wish to alter the membership, log in to your FiftyP account at <a href=\"http://www.fiftyp.com\">www.fiftyp.com </a> and click the <strong>Membership </strong>button.</p>\r\n\t<p>Questions? Please email us at <a href=\"mailto:support@fiftyp.com\">support@fiftyp.com </a>.</p>\r\n\t<p>Thanks for putting your child on the right path! </p>\r\n\t<p>&nbsp;\t  </p>\r\n\t  <p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tThe FiftyP Team<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t  </p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a><br>\r\n    </font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' WHERE `sys_template`.`sys_template_id`='ORDER_CONFIRMATION_NEW_CHILD_ONE_OR_THREE_YEAR';
UPDATE `sys_template`  SET `sys_template`.`design` = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" nowrap=\"nowrap\"><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\tOrder Confirmation of Share Transaction\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>\r\n\t  We are writing to confirm your recent donation to ${organization} in support of ${donationCause}. Your family can be proud of supporting this organization and its important work.     \r\n\t<p>Your donation of $${donationAmount}  has been processed. Here are the details:\r\n\t<p><br>\r\n\t    Date of Transaction: ${transactionDate?string(\"MMMM d, yyyy\")}<br>\r\n\t    Transaction #: ${transactionId}<br> \r\n\t\tPayment method: ${paymentMethod}, \r\n\r\n\r\n card #${ccDisplayNumber} </p>\r\n\t  <p>To learn more about FiftyP and our commitment to raising social awareness, visit us at <a href=\"http://www.fiftyp.com\">www.fiftyp.com </a> or check our FAQs at <a href=\"http://www.fiftyp.com/faq\">www.fiftyp.com/faq </a>. Still have questions? Email us at www.fiftyp.com/support . </p>\r\n\t  <p>Please keep us informed of any changes to your email address. You can update your information by logging in to your FiftyP account and clicking on �Your Profile�.</p>\r\n\t  <p>[The Carbon Fund] is a 501(c)(3) charitable organization. Please consider printing this confirmation for your records.</p>\r\n\t  <p>We appreciate your commitment to making a difference! </p>\r\n\t  <p>&nbsp;\t  </p>\r\n\t  <p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tThe FiftyP Team<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t  </p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a><br>\r\n    </font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' WHERE `sys_template`.`sys_template_id`='ORDER_CONFIRMATION_SHARE_TRANSACTION';
UPDATE `sys_template`  SET `sys_template`.`design` = '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\"\"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<title>FiftyP</title>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n</head>\r\n<body>\r\n<table width=\"600\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #7B2B83\">\r\n  <tr>\r\n    <td style=\"border-bottom:1px solid #7B2B83\">\r\n      <table width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <td><img src=\"cid:logo_parent.gif\" width=\"294\" height=\"84\"></td>\r\n          <td align=\"right\" style=\"padding:55px 10px 0px 0px\" nowrap=\"nowrap\"><font color=\"#F68B1F\" size=\"2\" style=\"font-size:14px;\" face=\"verdana\"><b>\r\n\t\t\t${request}\r\n\t\t  </b></font></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td style=\"padding:20px;font-size:12px; font-family:verdana;\">\r\n\t<p>Dear ${parentName},</p>\r\n\t<p>\r\n\t\t<b>A request is awaiting your approval at FiftyP.com!</b>\r\n\t\t<br/> \r\n\t\t<b>${childName}</b> ${childRequest}.<br>\r\n\t\tYou''ll find details on the Family Account Summary page at <a href=''http://www.fiftyp.com''>www.fiftyp.com</a>. \r\n\t\t<br/> \r\n\r\n</p>\r\n\t<br/>\t\t\t\r\n\t\t<p>\r\n\t\t\tWith best wishes,<br>\r\n\t\t\tThe FiftyP Team<br>\r\n\t\t\t<a href=''http://www.fiftyp.com''>www.fiftyp.com</a><br>\r\n\t\t</p>\r\n\t</td>\r\n  </tr>\r\n  <tr>\r\n    <td bgcolor=\"#7B2B83\" style=\"padding:5px\"> <font  style=\"font-size:11px; line-height:16px\" face=\"verdana\" color=\"#ffffff\">For more information please contact our support team at <a href=''http://www.fiftyp.com''>support@fiftyp.com</a></font> </td>\r\n  </tr>\r\n</table>\r\n\r\n</body>' WHERE `sys_template`.`sys_template_id`='REQUEST_SEND_TO_PARENT';

/* Updating text displayed in Hall of Sharing for cause donations */
UPDATE cause SET hos_summary_txt = 'to protect the environment by planting trees in India' WHERE cause_id = 1;
UPDATE cause SET hos_summary_txt = 'to protect the environment by rebuilding forests in Nicaragua' WHERE cause_id = 2;
UPDATE cause SET hos_summary_txt = 'to protect the environment by building wind turbines' WHERE cause_id = 3;
UPDATE cause SET hos_summary_txt = 'to protect the environment by supporting clean energy sources' WHERE cause_id = 4;

